const utils = require('@/utils')
const { compose } = require('@/server')

const call_handler_with_args = ([path, handler]) => (ctx, next) => {
  const args = {}
  const split_url = ctx.req.url.split('/')
  const split_path = path.split('/')

  for (let i = 0; i < split_path.length; i++) {
    const section = split_path[i]
    // is argument
    if (section.indexOf(':') === 0) {
      args[section.replace(':', '')] = split_url[i]
    }
  }

  ctx.params = args

  return handler(ctx, next)
}

/**
 * A Router is something that exposes a
 * callback function which is a Server Middleware
 *
 * The Router allows us to create RESTful handlers
 * for an API
 *
 * Example:
 *
 * const router = new Router()
 *
 * router
 *  .get('/foo', (ctx, next) => {
 *    ctx.log.trace('Called!')
 *    return next()
 *  })
 *  .get('/foo', (ctx, next) => {
 *    ctx.log.trace('I was called as well!')
 *
 *    ctx.status = 201
 *   })
 *
 * server.use(router.routes())
 */

class Router {
  constructor() {
    this._handlers = {
      get: [],
      post: [],
      patch: [],
      put: [],
      delete: []
    }
  }


  /**
   * @private
   */
  _addHandlersFor(method, path, ...handlers) {
    utils.invariant(
      method in this._handlers,
      `You tried to add a handler for a method that does not exists yet: ${method}`
    )

    if (typeof path === 'function') {
      this._handlers[method].push(...[path, ...handlers].map(handler => (['*', handler])))
    } else {
      utils.invariant(
        typeof path === 'string',
        `You tried to create a route handler with a first value of type ${typeof path} instead of string or function`
      )

      this._handlers[method].push(...handlers.map(handler => ([path, handler])))

    }


    return this
  }

  /**
   * Adds GET handler(s)
   * 
   * If given a string as first argument, all other arguments are
   * treated like middleware that is related only to that subpath.
   * 
   * If given all middleware, handles any _GET_ that this router sees
   * 
   * ## Example
   * 
   * ```js
   * router
   *   // this will get called on GET /foo, /foo?123=true,
   *   // & /foo/bar/baz/....
   *  .get('/foo', (ctx) => { ... })
   *   // this will get called on GET /foo, /bar, /baz, ...
   *  .get('/:name', (ctx, next) => {  ... }, ctx => {})
   * ```
   * 
   * @param {(string | import('@/server').Middleware)} path - If given a string, can use `/foo/:bar` for named params or even regex if you're lucky
   * @param  {...import('@/server').Middleware} handlers 
   * @returns {Router}
   */
  get(path, ...handlers) {
    return this._addHandlersFor('get', path, ...handlers)
  }

  /**
   * Adds POST handler(s)
   *
   * If given a string as first argument, all other arguments are
   * treated like middleware that is related only to that subpath.
   *
   * If given all middleware, handles any _POST_ that this router sees
   *
   * ## Example
   *
   * ```js
   * router
   *   // this will be called on POST /foo, /foo?123=true,
   *   // & /foo/bar/baz/....
   *  .post('/foo', (ctx) => { ... })
   *   // this will be called on POST /foo, /bar, /baz, ...
   *  .post('/:name', (ctx, next) => {  ... }, ctx => {})
   * ```
   *
   * @param {(string | import('@/server').Middleware)} path - If given a string, can use `/foo/:bar` for named params or even regex if you're lucky
   * @param  {...import('@/server').Middleware} handlers
   * @returns {Router}
   */
  post(path, ...handlers) {
    return this._addHandlersFor('post', path, ...handlers)
  }

  /**
   * Adds PATCH handler(s)
   *
   * If given a string as first argument, all other arguments are
   * treated like middleware that is related only to that subpath.
   *
   * If given all middleware, handles any _PATCH_ that this router sees
   *
   * ## Example
   *
   * ```js
   * router
   *   // this will be called on PATCH /foo, /foo?123=true,
   *   // & /foo/bar/baz/....
   *  .patch('/foo', (ctx) => { ... })
   *   // this will be called on PATCH /foo, /bar, /baz, ...
   *  .patch('/:name', (ctx, next) => {  ... }, ctx => {})
   * ```
   *
   * @param {(string | import('@/server').Middleware)} path - If given a string, can use `/foo/:bar` for named params or even regex if you're lucky
   * @param  {...import('@/server').Middleware} handlers
   * @returns {Router}
   */
  patch(path, ...handlers) {
    return this._addHandlersFor('patch', path, ...handlers)
  }

  /**
   * Adds PUT handler(s)
   *
   * If given a string as first argument, all other arguments are
   * treated like middleware that is related only to that subpath.
   *
   * If given all middleware, handles any _PUT_ that this router sees
   *
   * ## Example
   *
   * ```js
   * router
   *   // this will be called on PUT /foo, /foo?123=true,
   *   // & /foo/bar/baz/....
   *  .put('/foo', (ctx) => { ... })
   *   // this will be called on PUT /foo, /bar, /baz, ...
   *  .put('/:name', (ctx, next) => {  ... }, ctx => {})
   * ```
   *
   * @param {(string | import('@/server').Middleware)} path - If given a string, can use `/foo/:bar` for named params or even regex if you're lucky
   * @param  {...import('@/server').Middleware} handlers
   * @returns {Router}
   */
  put(path, ...handlers) {
    return this._addHandlersFor('put', path, ...handlers)
  }

  /**
   * Adds DELETE handler(s)
   *
   * If given a string as first argument, all other arguments are
   * treated like middleware that is related only to that subpath.
   *
   * If given all middleware, handles any _DELETE_ that this router sees
   *
   * ## Example
   *
   * ```js
   * router
   *   // this will be called on DELETE /foo, /foo?123=true,
   *   // & /foo/bar/baz/....
   *  .delete('/foo', (ctx) => { ... })
   *   // this will be called on DELETE /foo, /bar, /baz, ...
   *  .delete('/:name', (ctx, next) => {  ... }, ctx => {})
   * ```
   *
   * @param {(string | import('@/server').Middleware)} path - If given a string, can use `/foo/:bar` for named params or even regex if you're lucky
   * @param  {...import('@/server').Middleware} handlers
   * @returns {Router}
   */
  delete(path, ...handlers) {
    return this._addHandlersFor('delete', path, ...handlers)
  }

  route() {
    return (ctx, next) => {
      const { method, url } = ctx.req
      const handlers = this._handlers[method.toLowerCase()]

      // We have no handlers for this method
      if (!handlers.length) {
        return next()
      }

      const split_url = url.split('/')

      const matched_handlers_by_path = handlers.filter(([path]) => {
        const split_path = path.split('/')

        // The path that they requested is longer than
        // the URL so we DO NOT MATCH
        if (split_path.length > split_url.length) {
          return false
        }

        for (let i = 0; i < split_path.length; i++) {
          const part = split_path[i]
          // is an arg
          if (part.indexOf(':') === 0) {
            continue;
          } else if (part === split_url[i]) {
            // is a direct match
            continue
          } else if (new RegExp(part).test(split_url[i])) {
            continue
          } else {
            return false
          }
        }


        // if we got here, we matched enough
        return true
      })

      const handler = compose(matched_handlers_by_path.map(call_handler_with_args))

      return handler(ctx, next)
    }
  }
}

module.exports = Router